    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        // TODO add your handling code here:
        String Marca = textMarca.getText();
        String Tipo = textTipo.getText();
        float Preco = Float.parseFloat(textPreco.getText());
        
        Registro r1 = new Registro (Marca,Tipo,Preco);
        r1.Listar();
        
        SaidaRegistro t2 = new SaidaRegistro();
        t2.resultMarca.setText(Marca);
        t2.resultTipo.setText(Tipo);
        t2.resultPreco.setText(Float.toString(Preco));
        
        t2.setVisible(true);
    }//GEN-LAST:event_btnRegisterActionPerformed

